import React, { useState } from "react";
import axios from "axios";

export default function Body() {
  const [resultJson, setResultJson] = useState();
  const [searchInputValue, setSearchInputValue] = useState("");
  const [error, setError] = useState("");

  const sendRequest = () => {
    // fetch(`localhost:8000/${searchInputValue}`).then((r) => {
    axios(`https://swapi.dev/api/${searchInputValue}`)
      .then((r) => {
        setError("");
        setResultJson(r.data);
      })
      .catch((e) => {
        setResultJson();
        setError(e.message);
      });
  };
  return (
    <main>
      <div className="API">
        <input
          value={searchInputValue}
          onChange={(e) => {
            setSearchInputValue(e.target.value);
          }}
          class="form-control"
          placeholder="people/1/"
        ></input>
        <button onClick={sendRequest} className='btn'>request</button>
      </div>
      {/* {error && <div style={{ color: "red" }} className='JSONResponse'>{error}</div>} */}
      {error && <pre className="JSONResponse">{error} {JSON.stringify(resultJson, null, `\t`)}</pre>}
    </main>
  );
}
